mapper.select:
SELECT NO, ID AS ADMIN_ID, PWD AS ADMIN_PWD
FROM ADMIN
WHERE ID = #{adminId}
AND PWD = #{adminPwd}

mapper.update:
UPDATE NOTICE
SET DELETE_YN = 'Y'
WHERE NO = #{no}

mapper.select:
SELECT NO, NICK, GENDER, PHONE, EMAIL, TO_CHAR(POINT,'fm999,999,999,999') AS POINT, BANK, ACCOUNT, DEPOSITOR, STATUS, SUBSTR(ENROLL_DATE, 1, 8) AS ENROLL_DATE
FROM MEMBER
WHERE NO = #{no}

mapper.update:
UPDATE PROJECT
SET STATUS = '반려됨'
WHERE NO = #{no}
