mapper.insert:
INSERT INTO ATTENDANCE
(
	NO
	, ATT_DATE
)
VALUES
(
	#{no}
	, SYSDATE
)

mapper.select:
SELECT SUBSTR(ATT_DATE, 1, 8) AS ATT_DATE
FROM ATTENDANCE
WHERE NO = #{no}

SELECT COUNT(*)
FROM ATTENDANCE
WHERE NO = #{no}

SELECT COUNT(*)
FROM ATTENDANCE
WHERE NO = #{no}
AND ATT_DATE LIKE #{day}
