mapper.delete:
DELETE FROM PAY
WHERE NO = #{no}
AND M_NO = #{mNo}

mapper.select:
SELECT COUNT(*)
FROM PAY
WHERE M_NO = #{mNo}

SELECT P.NO, P.CHANGE_NAME, M.NICK AS CREATOR, C.NAME AS CATEGORY, P.TITLE, P.PRICE, (P.END_DATE)-(TRUNC(SYSDATE)) AS CAL_DATE, TO_CHAR((P.END_DATE+1), 'YYYY-MM-DD') AS PAY_DATE, P.SUMMARY, P.DELIVERY_DATE
FROM PROJECT P JOIN PRJ_CATEGORY C ON C.NO = P.CATEGORY JOIN MEMBER M ON M.NO = P.CREATOR
WHERE P.NO = #{pNo}

mapper.insert:
		 INSERT INTO PAY_LIST (
		        NO
		        , M_NO
		        , P_NO
		        , SET_NO
		        , PAY_NO
		        , ADDR_NO
		        , PRICE
		        , PAY_DATE
        )
        VALUES(
        PAY_LIST_NO_SEQ.NEXTVAL ,
		        #{mNo}
		        , #{pNo}
		        , #{setNo}
		        , #{payNo}
		        , #{addrNo}
		        , #{price}
				, #{payDate}
		)

mapper.select:
SELECT PL.NO , PL.M_NO, PL.P_NO, PL.SET_NO, PL.PAY_NO, PL.ADDR_NO, TO_CHAR(PL.RESERVE_DATE,'YYYY-MM-DD'), TO_CHAR(PL.PAY_DATE,'YYYY-MM-DD'), P.CHANGE_NAME, P.TITLE
FROM PAY_LIST PL JOIN PROJECT P ON P.NO = PL.P_NO
WHERE M_NO = #{mNo}
