mapper.select:
SELECT COUNT(NO)
FROM PROJECT 
WHERE CREATOR = #{creator}

mapper.update:
  	UPDATE MEMBER SET
		ACCOUNT = #{account},
		BANK = #{bank},
		DEPOSITOR = #{depositor},
		INFO = #{info}
	WHERE NO = #{no}

mapper.select:
	SELECT COUNT(PRJ_NO)
		FROM LIKE_PROJECT
        WHERE M_NO = #{mNo}
        AND PRJ_NO = #{prjNo}

	SELECT COUNT(LIKE_MEMBER_NO)
		FROM LIKE_MEMBER
        WHERE M_NO = #{mNo}
        AND LIKE_MEMBER_NO = #{likeMemberNo}
